export const getIconNameForTheadCell = (isSorted, isSortedAscending) => {
  if (isSorted) {
    return isSortedAscending ? 'ArrowDown' : 'ArrowUp';
  }

  return 'ArrowUpDown';
};
export const getAriaLabelForTheadCell = (name, fieldName, isSorted, isSortedAscending, t) => {
  let ariaLabel = fieldName;

  if (typeof name === 'string') {
    ariaLabel = isSorted ? isSortedAscending ? name.concat(' ', t('table.sorted_ascending')) : name.concat(' ', t('table.sorted_descending')) : name.concat(' ', t('table.sortable'));
  }

  return ariaLabel;
};